package Patterns;

public class second {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      for (int row=4; row>=1; row--)
      {
    	  for (int column=1; column<=row; column++)
    	  {
    		  System.out.print(column);
    	  }
    	  System.out.println();
      }
	}

}
