package Sample;

public class break1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=1;
        while (i<=5) {
        	System.out.println(i);
        	i++;
        	break;
        }
        System.out.println("working with loop");
        System.out.println(i);
	}

}
