package string;

public class contains {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s="appu i love you";
		System.out.println(s.contains("appu"));
		System.out.println(s.contains("i"));
		System.out.println(s.contains("love"));
		System.out.println(s.contains("You"));
	}

}
